$(function () {
  var path = location.pathname
  if (path == "/posts/index") {
    $(".post-index-tab").addClass("selectMenu")
  }
  else if (path == "/posts/new") {
    $(".post-new-tab").addClass("selectMenu")
  }
  else if (path == "/coupons/index") {
    $(".coupons-index-tab").addClass("selectMenu")
  }
});
