$(function () {
	$(".post-form").on("submit", function (e) {
		e.preventDefault();
		let inputText = $("#content").val();
		let url = $(this).attr("action");

		$.ajax({
			type: "POST",
			url: url,
			data: { content: inputText },
			dataType: "json",
		});
	});
});
