$(function () {
  var path = location.pathname
  if (path == "/posts/index") {
    $(".posts-index-tab").addClass("selectMenu")
  }
  else if (path == "/posts/new" || "/posts/create") {
    $(".new-post-tab").toggleClass("selectMenu")
  }
  else if (path == "/coupons/index") {
    $(".coupons-index-tab").addClass("selectMenu")
  }
});
